            "date": "sample Date",
            "toll": "sample Toll", 
            "tariff": "sample Tariff",
            "dateFormated":"sample dateFormated" }
    ];


        useEffect(()=>{
            fetchVehicleList();
            setVehicleList(vehicleList);    
        },[])

    const fetchVehicleList = async ()=>{
        const response=await fetch('/getVehicleList');
        const data=await response.json();
        console.log(data);
        setVehicleList(data);
    }

    const btnAddVehicle = () => {
        document.getElementById('vehicleForm').style.display="block";
    }
    const btnAddToll = () => {
