            settollList(()=>{
                const aget=JSON.parse(localStorage.getItem("tollListNew"));
                const a2={
                    "name":Math.random(),
                    "carSingle":60,   
                    "carReturn":32,
                    "lcvSingle":95,
                    "lcvReturn":52,
                    "truckSingle":205,
                    "truckReturn":152,
                    "heavySingle":320,
                    "heavyReturn":162
                }
                return [...aget,a2];
            })
            localStorage.setItem("tollListNew",JSON.stringify(tollList));
        }
        
        console.log(localStorage.getItem("tollListNew"));

        if (localStorage.getItem("vehicleListNew")==null){
            localStorage.setItem("vehicleListNew",JSON.stringify(v1));
            setVehicleList(()=>{
                return v1;
            });
        }else{
            setVehicleList(()=>{
                const vget=JSON.parse(localStorage.getItem("vehicleListNew"));
                const v2={   "type": "sample Type 2",
                "number": "sample Number 2",
                "date": "sample Date 2",
                "toll": "sample Toll 2", 
                "tariff": "sample Tariff 2", 
                "dateFormated":"sample dateFormated"};
                return [...vget,v2];
            })
            localStorage.setItem("vehicleListNew",JSON.stringify(vehicleList));
        }
        console.log(localStorage.getItem("vehicleListNew"));
    }

    const  test2= async ()=>{
        const response=await fetch("http://localhost:5000"+"/");
        const data=await response.text();
        console.log(data);
        let response1=await fetch("http://localhost:5000"+"/getVehicleList");
