    const reset = ()=>{   
        localStorage.setItem("tollListNew",JSON.stringify(a)); 
        settollList(()=>{
            return a;
        })
 
        localStorage.setItem("vehicleListNew",JSON.stringify(v1)); 
        setVehicleList(()=>{
            return v1;
        })
    }

    const addTollSubmit = (event) =>{
        event.preventDefault();



        const addTollJSON={
            "name":event.target[0].value,
            "carSingle":event.target[1].value,   
            "carReturn":event.target[2].value,
            "lcvSingle":event.target[3].value,
            "lcvReturn":event.target[4].value,
            "truckSingle":event.target[5].value,
            "truckReturn":event.target[6].value,
            "heavySingle":event.target[7].value,
            "heavyReturn":event.target[8].value
        }
        console.log(event);
        console.log(addTollJSON);
        document.getElementById('tollForm').style.display="none";
        alert("Toll added");

        let getToll =JSON.parse(localStorage.getItem("tollListNew"));
        getToll=[...getToll,addTollJSON];
        localStorage.setItem("tollListNew",JSON.stringify(getToll));
        settollList(()=>{
            return getToll;
        })
    }

    const calculateTariff=()=>{
        let vehicleType=document.getElementById("vehicleType").value;
        let tollName=document.getElementById("tollName").value;
        let dateTime=new Date().toLocaleString();
        let vehicleNumber=document.getElementById("vehicleNumber").value;
        let dateFormated=new Date();
        let tariff="";
        console.log(vehicleNumber+"  "+dateTime);
        console.log(vehicleList);
        vehicleList.forEach((a)=>{
            if(a.number===vehicleNumber){
                let newTime=new Date();
                if(dateFormated-newTime<=3600){
                    tollList.forEach((toll)=>{
                        if(toll.name===tollName){
                            if(vehicleType==="Car/Jeep/Van"){
                                tariff=toll.carReturn;
                            }else if(vehicleType==="LCV"){
                                tariff=toll.lcvReturn;
                            }else if(vehicleType==="Truck/Bus"){
                                tariff=toll.truckReturn;
                            }else{
                                tariff=toll.heavyReturn;
                            }
                        }
                    })
                }else{
                    tollList.forEach((toll)=>{
                        if(toll.name===tollName){
                            if(vehicleType==="Car/Jeep/Van"){
                                tariff=toll.carSingle;
                            }else if(vehicleType==="LCV"){
                                tariff=toll.lcvSingle;
                            }else if(vehicleType==="Truck/Bus"){
                                tariff=toll.truckSingle;
                            }else{
                                tariff=toll.heavySingle;
                            }
                        }
                    })
                }
                if(tariff!==""){
                    console.log("updating");    
                    document.getElementById("Tariff").value=tariff;
                }
                    
            }
        })


        newEntry={
            "type": vehicleType,
            "number": vehicleNumber,
            "date": dateTime,
            "toll": tollName, 
            "tariff": tariff,
            "dateFormated":dateFormated
        }

    }

    const addVehicleSubmit = (event)=>{
        event.preventDefault();

        document.getElementById('vehicleForm').style.display="none";
        alert("Vehicle added");

        let getVehicle =JSON.parse(localStorage.getItem("vehicleListNew"));
        getVehicle=[...getVehicle,newEntry];
        localStorage.setItem("vehicleListNew",JSON.stringify(getVehicle));
        setVehicleList(()=>{
            return getVehicle;
        })

    }    

    const searchWithName = ()=>{
        
    }


    return <DataContext.Provider value={{
        btnAddVehicle,
        btnAddToll,
        btnViewToll,
        btnAddVehicleClose,
        btnAddTollClose,
        test,
        tollList,
        settollList,
        reset,
        vehicleList,
        setVehicleList,
        addTollSubmit,
        test2,
        calculateTariff,
        addVehicleSubmit,
        searchWithName
    }}>{children}</DataContext.Provider>
}

export default DataContext;
