import { createContext, useState } from 'react';
import React from 'react';

const DataContext = createContext();

export const DataProvider = ({ children }) => {

    const [tollList,settollList]= useState([]);

    const btnAddVehicle = () => {
        console.log("1");
        document.getElementById('vehicleForm').style.display="block";
    }
    const btnAddToll = () => {
        console.log("2");
        document.getElementById('tollForm').style.display="block";
    }
    const btnViewToll = () => {
        console.log("3");
    }
    const btnAddVehicleClose=()=>{
        document.getElementById('vehicleForm').style.display="none";
    }

    const btnAddTollClose=()=>{
        document.getElementById('tollForm').style.display="none";
    }

    const test=()=>{
        if (localStorage.getItem("tollListNew")==null){
            localStorage.setItem("tollListNew",JSON.stringify(tollListNew));
            settollList(()=>{
                return tollListNew;
            });
        }else{
            settollList(()=>{
                const a=JSON.parse(localStorage.getItem("tollListNew"));
                return [...a,"toll2"];
            })
            localStorage.setItem("tollListNew",JSON.stringify(tollList));
        }
        console.log(tollList.map)
        
    }

    const tollListNew = ["toll1"];
    const a = [
        {   "type": "type1",
            "number": "number1",
            "date": "date1",
            "toll": "toll1", 
            "tariff": "tariff1" }, 
        {   "type": "type2",
            "number": "number2", 
            "date": "date2", 
            "toll": "toll2", 
            "tariff": "tariff2" }, 
        {   "type": "type3", 
            "number": "number3", 
            "date": "date3", 
            "toll": "toll3", 
            "tariff": "tariff3" }
    ];

    const reset = ()=>{
        localStorage.setItem("vehicleJSON",JSON.stringify(a));   
        localStorage.setItem("tollListNew",JSON.stringify(tollListNew)); 
    }

    
    
    localStorage.setItem("vehicleJSON",JSON.stringify(a));

    const vehicleJSON=JSON.parse(localStorage.getItem("vehicleJSON"));

    console.log(localStorage.getItem("hello"));


    return <DataContext.Provider value={{
        vehicleJSON,
        btnAddVehicle,
        btnAddToll,
        btnViewToll,
        btnAddVehicleClose,
        btnAddTollClose,
        test,
        tollList,
        settollList,
        reset
    }}>{children}</DataContext.Provider>
}

export default DataContext;
