import { createContext, useEffect, useState } from 'react';
import React from 'react';

const DataContext = createContext();

export const DataProvider = ({ children }) => {

    const [tollList,settollList]= useState([]);
    const [vehicleList,setVehicleList]=useState([]);

    useEffect(()=>{
        const v3=[];
        if (localStorage.getItem("tollListNew")==null){
            localStorage.setItem("tollListNew",JSON.stringify(tollListNew));
            settollList(()=>{
                return tollListNew;
            });
        }else{
            settollList(()=>{
                const a=JSON.parse(localStorage.getItem("tollListNew"));
                return [...a,"toll2"];
            })
            localStorage.setItem("tollListNew",JSON.stringify(tollList));
        }

        if (localStorage.getItem("vehicleListNew")==null){
            localStorage.setItem("vehicleListNew",JSON.stringify(v1));
            setVehicleList(()=>{
                return v1;
            });
        }else{
            setVehicleList(()=>{
                const v1=JSON.parse(localStorage.getItem("vehicleListNew"));
                const v2={   "type": "sample Type 2",
                "number": "sample Number 2",
                "date": "sample Date 2",
                "toll": "sample Toll 2", 
                "tariff": "sample Tariff 2" };
                v3=[...v1,v2]
                return v3;
            })
            
            localStorage.setItem("vehicleListNew",JSON.stringify(v3));
        }
    },[])

    const btnAddVehicle = () => {
        console.log("1");
        document.getElementById('vehicleForm').style.display="block";
    }
    const btnAddToll = () => {
        console.log("2");
        document.getElementById('tollForm').style.display="block";
    }
    const btnViewToll = () => {
        console.log("3");
    }
    const btnAddVehicleClose=()=>{
        document.getElementById('vehicleForm').style.display="none";
    }

    const btnAddTollClose=()=>{
        document.getElementById('tollForm').style.display="none";
    }

    const test=()=>{
        const v3=[];
        if (localStorage.getItem("tollListNew")==null){
            localStorage.setItem("tollListNew",JSON.stringify(tollListNew));
            settollList(()=>{
                return tollListNew;
            });
        }else{
            settollList(()=>{
                const a=JSON.parse(localStorage.getItem("tollListNew"));
                return [...a,"toll2"];
            })
            localStorage.setItem("tollListNew",JSON.stringify(tollList));
        }
        
        if (localStorage.getItem("vehicleListNew")==null){
            localStorage.setItem("vehicleListNew",JSON.stringify(v1));
            setVehicleList(()=>{
                return v1;
            });
        }else{
            setVehicleList(()=>{
                const v1=JSON.parse(localStorage.getItem("vehicleListNew"));
                const v2={   "type": "sample Type 2",
                "number": "sample Number 2",
                "date": "sample Date 2",
                "toll": "sample Toll 2", 
                "tariff": "sample Tariff 2" };
                return [...v1,v2];
            })
            localStorage.setItem("vehicleListNew",JSON.stringify(vehicleList));
        }
        console.log(vehicleList)
    }

    const tollListNew = ["sample toll"];
    const a = ["sample toll"];
    const v1 = [
        {   "type": "sample Type",
            "number": "sample Number",
            "date": "sample Date",
            "toll": "sample Toll", 
            "tariff": "sample Tariff" }
    ];

    const reset = ()=>{
        localStorage.setItem("vehicleJSON",JSON.stringify(a));   
        localStorage.setItem("tollListNew",JSON.stringify(["sample toll"])); 
        settollList(()=>{
            return ["sample toll"];
        })
 
        localStorage.setItem("VehicleListNew",JSON.stringify(a)); 
        setVehicleList(()=>{
            return v1;
        })
    }

    
    
    localStorage.setItem("vehicleJSON",JSON.stringify(a));

    const vehicleJSON=JSON.parse(localStorage.getItem("vehicleJSON"));

    console.log(localStorage.getItem("hello"));


    return <DataContext.Provider value={{
        vehicleJSON,
        btnAddVehicle,
        btnAddToll,
        btnViewToll,
        btnAddVehicleClose,
        btnAddTollClose,
        test,
        tollList,
        settollList,
        reset,
        vehicleList,
        setVehicleList
    }}>{children}</DataContext.Provider>
}

export default DataContext;
