import { createContext, useEffect, useState } from 'react';
import React from 'react';

const DataContext = createContext();

export const DataProvider = ({ children }) => {

    const [tollList,settollList]= useState([]);
    const [vehicleList,setVehicleList]=useState([]);

    const a = [{
        "name":Math.random(),
        "carSingle":60,   
        "carReturn":32,
        "lcvSingle":95,
        "lcvReturn":52,
        "truckSingle":205,
        "truckReturn":152,
        "heavySingle":320,
        "heavyReturn":162
    }];
    const v1 = [
        {   "type": "sample Type",
            "number": "sample Number",
            "date": "sample Date",
            "toll": "sample Toll", 
            "tariff": "sample Tariff" }
    ];


        useEffect(()=>{
            if (localStorage.getItem("tollListNew")==null){
                localStorage.setItem("tollListNew",JSON.stringify(a));
                settollList(()=>{
                    console.log("Inside settollList empty")
                    return a;
                });
            }else{
                settollList(()=>{
                    console.log("Inside settollList")
                    console.log(JSON.parse(localStorage.getItem("tollListNew")));
                    return JSON.parse(localStorage.getItem("tollListNew"));
                })
                localStorage.setItem("tollListNew",JSON.stringify(tollList));
            }
            

            if (localStorage.getItem("vehicleListNew")==null){
                localStorage.setItem("vehicleListNew",JSON.stringify(v1));
                setVehicleList(()=>{
                    console.log("Inside setVehicleList empty")
                    return v1;
                });
            }else{
                setVehicleList(()=>{
                    console.log("Inside setVehicleList")
                    console.log(localStorage.getItem("vehicleListNew"));
                    return JSON.parse(localStorage.getItem("vehicleListNew"));
                })
                
                localStorage.setItem("vehicleListNew",JSON.stringify(vehicleList));
            }
            
            
        console.log(localStorage.getItem("tollListNew"));
        
        console.log(localStorage.getItem("vehicleListNew"));

        },[])

    const btnAddVehicle = () => {
        document.getElementById('vehicleForm').style.display="block";
    }
    const btnAddToll = () => {
        document.getElementById('tollForm').style.display="block";
    }
    const btnViewToll = () => {
        console.log("3");
    }
    const btnAddVehicleClose=()=>{
        document.getElementById('vehicleForm').style.display="none";
    }

    const btnAddTollClose=()=>{
        document.getElementById('tollForm').style.display="none";
    }

    const test=()=>{
        if (localStorage.getItem("tollListNew")==null){
            localStorage.setItem("tollListNew",JSON.stringify(a));
            settollList(()=>{
                return a;
            });
        }else{
            settollList(()=>{
                const aget=JSON.parse(localStorage.getItem("tollListNew"));
                const a2={
                    "name":Math.random(),
                    "carSingle":60,   
                    "carReturn":32,
                    "lcvSingle":95,
                    "lcvReturn":52,
                    "truckSingle":205,
                    "truckReturn":152,
                    "heavySingle":320,
                    "heavyReturn":162
                }
                return [...aget,a2];
            })
            localStorage.setItem("tollListNew",JSON.stringify(tollList));
        }
        
        console.log(localStorage.getItem("tollListNew"));

        if (localStorage.getItem("vehicleListNew")==null){
            localStorage.setItem("vehicleListNew",JSON.stringify(v1));
            setVehicleList(()=>{
                return v1;
            });
        }else{
            setVehicleList(()=>{
                const vget=JSON.parse(localStorage.getItem("vehicleListNew"));
                const v2={   "type": "sample Type 2",
                "number": "sample Number 2",
                "date": "sample Date 2",
                "toll": "sample Toll 2", 
                "tariff": "sample Tariff 2" };
                return [...vget,v2];
            })
            localStorage.setItem("vehicleListNew",JSON.stringify(vehicleList));
        }
        console.log(localStorage.getItem("vehicleListNew"));
    }


    const test2=()=>{
        var tollListItems=JSON.parse(localStorage.getItem("tollListNew") || "[]")
        var tollListItem={
            "name":Math.random(),
            "carSingle":60,   
            "carReturn":32,
            "lcvSingle":95,
            "lcvReturn":52,
            "truckSingle":205,
            "truckReturn":152,
            "heavySingle":320,
            "heavyReturn":162
        };
        tollListItems.push(tollListItem);
        localStorage.setItem("tollListNew",JSON.stringify(tollListItems));
        settollList(()=>{
            return tollListItems;
        });

        console.log(localStorage.getItem("tollListNew"));

        var vehicleListItems=JSON.parse(localStorage.getItem("vehicleListNew") || "[]")
        var vehicleListItem={   "type": "sample Type 2",
        "number": "sample Number 2",
        "date": "sample Date 2",
        "toll": "sample Toll 2", 
        "tariff": "sample Tariff 2" };
        vehicleListItems.push(vehicleListItem);

        localStorage.setItem("vehicleListNew",JSON.stringify(vehicleListItems));
        setVehicleList(()=>{
            return vehicleListItems;
        })

        if (localStorage.getItem("vehicleListNew")==null){
            localStorage.setItem("vehicleListNew",JSON.stringify(v1));
            setVehicleList(()=>{
                return v1;
            });
        }else{
            setVehicleList(()=>{
                const vget=JSON.parse(localStorage.getItem("vehicleListNew"));
                const v2={   "type": "sample Type 2",
                "number": "sample Number 2",
                "date": "sample Date 2",
                "toll": "sample Toll 2", 
                "tariff": "sample Tariff 2" };
                return [...vget,v2];
            })
            localStorage.setItem("vehicleListNew",JSON.stringify(vehicleList));
        }
        console.log(localStorage.getItem("vehicleListNew"));
    }

    const reset = ()=>{   
        localStorage.setItem("tollListNew",JSON.stringify(a)); 
        settollList(()=>{
            return a;
        })
 
        localStorage.setItem("vehicleListNew",JSON.stringify(v1)); 
        setVehicleList(()=>{
            return v1;
        })
    }

    const addTollSubmit = (event) =>{
        event.preventDefault();



        const addTollJSON={
            "name":event.target[0].value,
            "carSingle":event.target[1].value,   
            "carReturn":event.target[2].value,
            "lcvSingle":event.target[3].value,
            "lcvReturn":event.target[4].value,
            "truckSingle":event.target[5].value,
            "truckReturn":event.target[6].value,
            "heavySingle":event.target[7].value,
            "heavyReturn":event.target[8].value
        }
        console.log(event);
        console.log(addTollJSON);
        document.getElementById('tollForm').style.display="none";
        alert("Toll added");

        let getToll =JSON.parse(localStorage.getItem("tollListNew"));
        getToll=[...getToll,addTollJSON];
        localStorage.setItem("tollListNew",JSON.stringify(getToll));
        settollList(()=>{
            return getToll;
        })
    }

    const calculateTariff=()=>{
        let vehicleType=document.getElementById("vehicleType").value;
        let tollName=document.getElementById("tollName").value;
        let dateTime=new Date().toLocaleString();
        let vehicleNumber=document.getElementById("vehicleNumber").value;
        console.log(vehicleNumber+"  "+dateTime+10);

        vehicleList.forEach((a)=>{
            if(a.number==vehicleNumber){
                let oldTime=a.dateTime;
                let newTime=new Date().toLocaleString();
            }
        })


        const newEntry={
            "type": vehicleType,
            "number": vehicleNumber,
            "date": dateTime,
            "toll": tollName, 
            "tariff": "sample Tariff 2"
        }

    }

    


    return <DataContext.Provider value={{
        btnAddVehicle,
        btnAddToll,
        btnViewToll,
        btnAddVehicleClose,
        btnAddTollClose,
        test,
        tollList,
        settollList,
        reset,
        vehicleList,
        setVehicleList,
        addTollSubmit,
        test2,
        calculateTariff
    }}>{children}</DataContext.Provider>
}

export default DataContext;
