import { createContext, useEffect, useState } from 'react';
import React from 'react';
import { useNavigate } from 'react-router';

const DataContext = createContext();

export const DataProvider = ({ children }) => {

    const [tollList,settollList]= useState([]);
    const [vehicleList,setVehicleList]=useState([]);
    const serverURL="https://zoho-js-project.herokuapp.com";
    let newEntry;

    const a = [{
        "name":Math.random(),
        "carSingle":60,   
        "carReturn":32,
        "lcvSingle":95,
        "lcvReturn":52,
        "truckSingle":205,
        "truckReturn":152,
        "heavySingle":320,
        "heavyReturn":162
    }];
    const v1 = [
        {   "type": "sample Type",
            "number": "sample Number",
            "date": "sample Date",
            "toll": "sample Toll", 
            "tariff": "sample Tariff",
            "dateFormated":"sample dateFormated" }
    ];


        useEffect(()=>{
            fetchVehicleList();    
            fetchTollList();
        },[])

    const fetchVehicleList = async ()=>{
        const response=await fetch("http://localhost:5000"+'/getVehicleList');
        const data=await response.json();
        console.log(data);
        setVehicleList(data);
    }

    const fetchTollList = async ()=>{
        const response=await fetch("http://localhost:5000"+'/getTollList');
        const data=await response.json();
        console.log(data);
        settollList(data);
    }

    const fetchVehicleListWithfilter = async (value)=>{
        const response=await fetch("http://localhost:5000"+`/getVehicleListWithfilter/${value}`);
        const data=await response.json();
        console.log(data);
        setVehicleList(data);
    }

    const btnAddVehicle = () => {
        document.getElementById('vehicleForm').style.display="block";
    }
    const btnAddToll = () => {
        document.getElementById('tollForm').style.display="block";
    }
    const btnViewToll = () => {
        console.log("3");
    }
    const btnAddVehicleClose=()=>{
        document.getElementById('vehicleForm').style.display="none";
    }

    const btnAddTollClose=()=>{
        document.getElementById('tollForm').style.display="none";
    }

    const test=()=>{
        if (localStorage.getItem("tollListNew")==null){
            localStorage.setItem("tollListNew",JSON.stringify(a));
            settollList(()=>{
                return a;
            });
        }else{
            settollList(()=>{
                const aget=JSON.parse(localStorage.getItem("tollListNew"));
                const a2={
                    "name":Math.random(),
                    "carSingle":60,   
                    "carReturn":32,
                    "lcvSingle":95,
                    "lcvReturn":52,
                    "truckSingle":205,
                    "truckReturn":152,
                    "heavySingle":320,
                    "heavyReturn":162
                }
                return [...aget,a2];
            })
            localStorage.setItem("tollListNew",JSON.stringify(tollList));
        }
        
        console.log(localStorage.getItem("tollListNew"));

        if (localStorage.getItem("vehicleListNew")==null){
            localStorage.setItem("vehicleListNew",JSON.stringify(v1));
            setVehicleList(()=>{
                return v1;
            });
        }else{
            setVehicleList(()=>{
                const vget=JSON.parse(localStorage.getItem("vehicleListNew"));
                const v2={   "type": "sample Type 2",
                "number": "sample Number 2",
                "date": "sample Date 2",
                "toll": "sample Toll 2", 
                "tariff": "sample Tariff 2", 
                "dateFormated":"sample dateFormated"};
                return [...vget,v2];
            })
            localStorage.setItem("vehicleListNew",JSON.stringify(vehicleList));
        }
        console.log(localStorage.getItem("vehicleListNew"));
    }

    const  test2= async ()=>{
        const response=await fetch("http://localhost:5000"+"/");
        const data=await response.text();
        console.log(data);
        let response1=await fetch("http://localhost:5000"+"/getVehicleList");
        let data1=await response1.json();
        console.log(data1);
    }
    // const test2=()=>{
    //     var tollListItems=JSON.parse(localStorage.getItem("tollListNew") || "[]")
    //     var tollListItem={
    //         "name":Math.random(),
    //         "carSingle":60,   
