    //             return [...vget,v2];
    //         })
    //         localStorage.setItem("vehicleListNew",JSON.stringify(vehicleList));
    //     }
    //     console.log(localStorage.getItem("vehicleListNew"));
    // }

    const reset = ()=>{   
        fetchVehicleList();
    }

    const addTollSubmit = (event) =>{
        event.preventDefault();



        const addTollJSON={
            "name":event.target[0].value,
            "carSingle":event.target[1].value,   
            "carReturn":event.target[2].value,
            "lcvSingle":event.target[3].value,
            "lcvReturn":event.target[4].value,
            "truckSingle":event.target[5].value,
            "truckReturn":event.target[6].value,
            "heavySingle":event.target[7].value,
            "heavyReturn":event.target[8].value
        }
        console.log(event);
        console.log(addTollJSON);
        postToll(addTollJSON);
        document.getElementById('tollForm').style.display="none";
        alert("Toll added");
        fetchTollList();
    }

    const calculateTariff=()=>{
        let vehicleType=document.getElementById("vehicleType").value;
        let tollName=document.getElementById("tollName").value;
        let dateTime=new Date().toLocaleString();
        let vehicleNumber=document.getElementById("vehicleNumber").value;
        let dateFormated=Date.now()/1000;
        let tariff="";
        console.log(vehicleNumber+"  "+dateTime);
        console.log(vehicleList);
        let vehiclefound=0;
        vehicleList.forEach((a)=>{
            if(a.number===vehicleNumber){
                let newTime=new Date();
                console.log("dateFormateed"+dateFormated);
                console.log("a.dateFormated"+a.dateFormated);
                console.log(parseInt(dateFormated)-parseInt(a.dateFormated));
                if(parseInt(dateFormated)-parseInt(a.dateFormated)<=3600  && a.toll===tollName ){
                    vehiclefound=1;
                    console.log("vehicle Found");
                    tollList.forEach((toll)=>{
                        if(toll.name===tollName){
                            if(vehicleType==="Car/Jeep/Van"){
                                tariff=toll.carReturn;
                            }else if(vehicleType==="LCV"){
                                tariff=toll.lcvReturn;
                            }else if(vehicleType==="Truck/Bus"){
                                tariff=toll.truckReturn;
                            }else{
                                tariff=toll.heavyReturn;
                            }
                        }
                    })
                }
            }
        })
        if(vehiclefound===0){
            tollList.forEach((toll)=>{
                if(toll.name===tollName){
                    if(vehicleType==="Car/Jeep/Van"){
                        tariff=toll.carSingle;
                    }else if(vehicleType==="LCV"){
                        tariff=toll.lcvSingle;
                    }else if(vehicleType==="Truck/Bus"){
                        tariff=toll.truckSingle;
                    }else{
                        tariff=toll.heavySingle;
                    }
                }
            })
        }
        
                
        if(tariff!==""){
            console.log("updating");    
            document.getElementById("Tariff").value=tariff;
        }
                
        newEntry={
            "type": vehicleType,
            "number": vehicleNumber,
            "date": dateTime,
            "toll": tollName, 
            "tariff": tariff,
            "dateFormated":dateFormated
        }
        console.log(newEntry);

    }

    const postVehicle = async(newEntry)=>{
        const response=await fetch("http://localhost:5000"+'/postVehicle',{
            method:"POST",
            headers:{
                "Content-Type":"application/json"
            },
            body:JSON.stringify(newEntry)
        })
    }

    const postToll = async(newEntry)=>{
        const response=await fetch("http://localhost:5000"+'/postToll',{
            method:"POST",
            headers:{
                "Content-Type":"application/json"
            },
            body:JSON.stringify(newEntry)
        })
    }

    const addVehicleSubmit = (event)=>{
        event.preventDefault();

        document.getElementById('vehicleForm').style.display="none";
        postVehicle(newEntry);
        alert("Vehicle added");
        document.getElementById("formVehicle").reset();
        console.log(newEntry);
        fetchVehicleList();
    }    

    const filterWithVehicleName = ()=>{
        let value=document.getElementById("searchValue").value;
        if(value==="")
            return
        fetchVehicleListWithfilter(value);
    }


    return <DataContext.Provider value={{
        btnAddVehicle,
        btnAddToll,
        btnViewToll,
        btnAddVehicleClose,
        btnAddTollClose,
        test,
        tollList,
        settollList,
        reset,
        vehicleList,
        setVehicleList,
        addTollSubmit,
        test2,
        calculateTariff,
        addVehicleSubmit,
        filterWithVehicleName
    }}>{children}</DataContext.Provider>
}

export default DataContext;
