import { createContext, useEffect, useState } from 'react';
import React from 'react';

const DataContext = createContext();

export const DataProvider = ({ children }) => {

    const [tollList,settollList]= useState([]);
    const [vehicleList,setVehicleList]=useState([]);

    const a = [{
        "name":"sample toll",
        "car":"60/30",
        "lcv":"95/50",
        "truck":"205/100",
        "heavy":"320/160"
    }];
    const v1 = [
        {   "type": "sample Type",
            "number": "sample Number",
            "date": "sample Date",
            "toll": "sample Toll", 
            "tariff": "sample Tariff" }
    ];

    

        // useEffect(()=>{
        //     if (window.localStorage.getItem("tollListNew")==null){
        //         window.localStorage.setItem("tollListNew",JSON.stringify(a));
        //         settollList(()=>{
        //             return a;
        //         });
        //     }else{
        //         settollList(()=>{
        //             return JSON.parse(window.localStorage.getItem("tollListNew"));
        //         })
        //         window.localStorage.setItem("tollListNew",JSON.stringify(tollList));
        //     }

        //     if (window.localStorage.getItem("vehicleListNew")==null){
        //         window.localStorage.setItem("vehicleListNew",JSON.stringify(v1));
        //         setVehicleList(()=>{
        //             return v1;
        //         });
        //     }else{
        //         setVehicleList(()=>{
        //             return JSON.parse(window.localStorage.getItem("vehicleListNew"));
        //         })
                
        //         window.localStorage.setItem("vehicleListNew",JSON.stringify(vehicleList));
        //     }
            
            
        // console.log(window.localStorage.getItem("tollListNew"));
        
        // console.log(window.localStorage.getItem("vehicleListNew"));

        // },[])

    const btnAddVehicle = () => {
        document.getElementById('vehicleForm').style.display="block";
    }
    const btnAddToll = () => {
        document.getElementById('tollForm').style.display="block";
    }
    const btnViewToll = () => {
        console.log("3");
    }
    const btnAddVehicleClose=()=>{
        document.getElementById('vehicleForm').style.display="none";
    }

    const btnAddTollClose=()=>{
        document.getElementById('tollForm').style.display="none";
    }

    const test=()=>{
        if (window.localStorage.getItem("tollListNew")==null){
            window.localStorage.setItem("tollListNew",JSON.stringify(a));
            settollList(()=>{
                return a;
            });
        }else{
            settollList(()=>{
                const aget=JSON.parse(window.localStorage.getItem("tollListNew"));
                const a2={
                    "name":"sample toll-2",
                    "car":"60/32",
                    "lcv":"95/52",
                    "truck":"205/152",
                    "heavy":"320/162"
                }
                return [...aget,a2];
            })
            window.localStorage.setItem("tollListNew",JSON.stringify(tollList));
        }
        
        console.log(window.localStorage.getItem("tollListNew"));

        if (window.localStorage.getItem("vehicleListNew")==null){
            window.localStorage.setItem("vehicleListNew",JSON.stringify(v1));
            setVehicleList(()=>{
                return v1;
            });
        }else{
            setVehicleList(()=>{
                const vget=JSON.parse(window.localStorage.getItem("vehicleListNew"));
                const v2={   "type": "sample Type 2",
                "number": "sample Number 2",
                "date": "sample Date 2",
                "toll": "sample Toll 2", 
                "tariff": "sample Tariff 2" };
                return [...vget,v2];
            })
            window.localStorage.setItem("vehicleListNew",JSON.stringify(vehicleList));
        }
        console.log(window.localStorage.getItem("vehicleListNew"));
    }


    const reset = ()=>{   
        window.localStorage.setItem("tollListNew",JSON.stringify(a)); 
        settollList(()=>{
            return a;
        })
 
        window.localStorage.setItem("vehicleListNew",JSON.stringify(v1)); 
        setVehicleList(()=>{
            return v1;
        })

    }

    const addTollSubmit = (event) =>{
        event.preventDefault();



        const addTollJSON={
            "name":event.target[0].value,   
            "car":event.target[1].value+"/"+event.target[2].value,
            "lcv":event.target[3].value+"/"+event.target[4].value,
            "truck":event.target[5].value+"/"+event.target[6].value,
            "heavy":event.target[7].value+"/"+event.target[8].value
        }
        console.log(event);
        console.log(addTollJSON);
        document.getElementById('tollForm').style.display="none";
        alert("Toll added");

        let getToll =JSON.parse(window.localStorage.getItem("tollListNew"));
        getToll=[...getToll,addTollJSON];
        window.localStorage.setItem("tollListNew",JSON.stringify(getToll));
        settollList(()=>{
            return getToll;
        })
    }

    


    return <DataContext.Provider value={{
        btnAddVehicle,
        btnAddToll,
        btnViewToll,
        btnAddVehicleClose,
        btnAddTollClose,
        test,
        tollList,
        settollList,
        reset,
        vehicleList,
        setVehicleList,
        addTollSubmit
    }}>{children}</DataContext.Provider>
}

export default DataContext;
